import{_ as e,o as t,c as r,R as a}from"./chunks/framework.zUbWieqp.js";const _=JSON.parse('{"title":"1. Prometheus-alert介绍","description":"","frontmatter":{},"headers":[],"relativePath":"guide/Linux/Monitor/Prometheus-Alert/index.md","filePath":"guide/Linux/Monitor/Prometheus-Alert/index.md","lastUpdated":1733992943000}'),o={name:"guide/Linux/Monitor/Prometheus-Alert/index.md"},s=a('<img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202412121037343.png" alt="Docker Compose" style="margin:0 auto;"><h1 id="_1-prometheus-alert介绍" tabindex="-1">1. Prometheus-alert介绍 <a class="header-anchor" href="#_1-prometheus-alert介绍" aria-label="Permalink to &quot;1. Prometheus-alert介绍&quot;">​</a></h1><p><a href="https://github.com/feiyu563/PrometheusAlert" target="_blank" rel="noreferrer">官当</a></p><p>PrometheusAlert是开源的运维告警中心消息转发系统,支持主流的监控系统Prometheus,日志系统Graylog和数据可视化系统Grafana发出的预警消息,支持将收到的这些消息发送到钉钉,短信和语音提醒等</p><h2 id="_1-1-出现的原因" tabindex="-1">1.1 出现的原因？ <a class="header-anchor" href="#_1-1-出现的原因" aria-label="Permalink to &quot;1.1 出现的原因？&quot;">​</a></h2><p>prometheus alertmanager 已有微信钉钉报警了，为什么还用prometheus-alert，它的出现整合了市面上的各种报警。它发出的预警消息,支持钉钉,微信,华为云短信,腾讯云短信,腾讯云电话,阿里云短信,阿里云电话等，可以看出<code>prometheusalert</code>相比和<code>alertmanager</code>内置的报警媒介相比支持的更全面，并且配置和调试更方便，<code>prometheusalert</code>并不能取代<code>alertmanager</code>，而是要作为<code>webhoook</code>与<code>alertmanager</code>结合使用。</p><p><a href="http://www.lishuai.fun/2021/01/27/prometheusalert/#/%E9%80%9A%E8%BF%87Prometheus-AlertManager-router%E6%96%B9%E5%BC%8F" target="_blank" rel="noreferrer">http://www.lishuai.fun/2021/01/27/prometheusalert/#/通过Prometheus-AlertManager-router方式</a></p>',7),n=[s];function l(h,i,u,c,d,m){return t(),r("div",null,n)}const g=e(o,[["render",l]]);export{_ as __pageData,g as default};
