import{_ as e,o as s,c as t,R as i}from"./chunks/framework.zUbWieqp.js";const u=JSON.parse('{"title":"一、dd命令的解释","description":"","frontmatter":{},"headers":[],"relativePath":"guide/Linux/cmd/9-dd.md","filePath":"guide/Linux/cmd/9-dd.md","lastUpdated":1712053412000}'),a={name:"guide/Linux/cmd/9-dd.md"},l=i('<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">dd if=/dev/nvme2n1 | gzip &gt; /data/nvme2n1img.gz</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">dd if=/dev/nvme2n1 | gzip &gt; /data/nvme2n1img.gz</span></span></code></pre></div><h1 id="一、dd命令的解释" tabindex="-1">一、dd命令的解释 <a class="header-anchor" href="#一、dd命令的解释" aria-label="Permalink to &quot;一、dd命令的解释&quot;">​</a></h1><p>dd：用指定大小的块拷贝一个文件，并在拷贝的同时进行指定的转换。</p><p>注意：指定数字的地方若以下列字符结尾，则乘以相应的数字：b=512；c=1；k=1024；w=2</p><p><em><strong>*参数注释：*</strong></em></p><ol><li>if=文件名：输入文件名，缺省为标准输入。即指定源文件。&lt; if=input file &gt;</li><li>of=文件名：输出文件名，缺省为标准输出。即指定目的文件。&lt; of=output file &gt;</li><li>ibs=bytes：一次读入bytes个字节，即指定一个块大小为bytes个字节。</li></ol><p>obs=bytes：一次输出bytes个字节，即指定一个块大小为bytes个字节。</p><p>bs=bytes：同时设置读入/输出的块大小为bytes个字节。</p><ol start="4"><li>cbs=bytes：一次转换bytes个字节，即指定转换缓冲区大小。</li><li>skip=blocks：从输入文件开头跳过blocks个块后再开始复制。</li><li>seek=blocks：从输出文件开头跳过blocks个块后再开始复制。</li></ol><p>注意：通常只用当输出文件是磁盘或磁带时才有效，即备份到磁盘或磁带时才有效。</p><ol start="7"><li>count=blocks：仅拷贝blocks个块，块大小等于ibs指定的字节数。</li><li>conv=conversion：用指定的参数转换文件。</li></ol><p>ascii：转换ebcdic为ascii</p><p>ebcdic：转换ascii为ebcdic</p><p>ibm：转换ascii为alternate ebcdic</p><p>block：把每一行转换为长度为cbs，不足部分用空格填充</p><p>unblock：使每一行的长度都为cbs，不足部分用空格填充</p><p>lcase：把大写字符转换为小写字符</p><p>ucase：把小写字符转换为大写字符</p><p>swab：交换输入的每对字节</p><p>noerror：出错时不停止</p><p>notrunc：不截短输出文件</p><p>sync：将每个输入块填充到ibs个字节，不足部分用空（NUL）字符补齐</p><p><a href="https://blog.csdn.net/qq_33160790/article/details/77488160" target="_blank" rel="noreferrer">https://blog.csdn.net/qq_33160790/article/details/77488160</a></p>',23),c=[l];function p(d,o,n,r,b,_){return s(),t("div",null,c)}const m=e(a,[["render",p]]);export{u as __pageData,m as default};
