import{_ as e,o as r,c as t,R as a}from"./chunks/framework.zUbWieqp.js";const _=JSON.parse('{"title":"1.介绍RADIUS","description":"","frontmatter":{},"headers":[],"relativePath":"guide/Linux/vpn/18-FreeRADIUS.md","filePath":"guide/Linux/vpn/18-FreeRADIUS.md","lastUpdated":1701595065000}'),i={name:"guide/Linux/vpn/18-FreeRADIUS.md"},o=a('<h1 id="_1-介绍radius" tabindex="-1">1.介绍RADIUS <a class="header-anchor" href="#_1-介绍radius" aria-label="Permalink to &quot;1.介绍RADIUS&quot;">​</a></h1><p>RADIUS：Remote Authentication Dial In User Service，<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/%E8%BF%9C%E7%A8%8B%E7%94%A8%E6%88%B7/1858887" target="_blank" rel="noreferrer">远程用户</a>拨号认证系统由RFC2865，RFC2866定义，是应用最广泛的<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/AAA%E5%8D%8F%E8%AE%AE" target="_blank" rel="noreferrer">AAA协议</a>。AAA是一种管理框架，因此，它可以用多种协议来实现。在实践中，人们最常使用远程访问拨号用户服务（Remote Authentication Dial In User Service，RADIUS）来实现AAA。</p><p>RADIUS是一种<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/C%2FS%E7%BB%93%E6%9E%84/4868476" target="_blank" rel="noreferrer">C/S结构</a>的协议，它的<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/%E5%AE%A2%E6%88%B7%E7%AB%AF/101081" target="_blank" rel="noreferrer">客户端</a>最初就是<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/NAS" target="_blank" rel="noreferrer">NAS</a>（Net Access Server）<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/%E6%9C%8D%E5%8A%A1%E5%99%A8/100571" target="_blank" rel="noreferrer">服务器</a>，任何运行RADIUS客户端软件的计算机都可以成为RADIUS的客户端。<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/RADIUS%E5%8D%8F%E8%AE%AE/1331757" target="_blank" rel="noreferrer">RADIUS协议</a>认证机制灵活，可以采用<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/PAP" target="_blank" rel="noreferrer">PAP</a>、<a href="https://link.zhihu.com/?target=https%3A//baike.baidu.com/item/CHAP" target="_blank" rel="noreferrer">CHAP</a>或者Unix登录认证等多种方式。RADIUS是一种可扩展的协议，它进行的全部工作都是基于Attribute-Length-Value的向量进行的。RADIUS也支持厂商扩充厂家专有属性</p><p>cp /usr/lib64/php/modules/mysql.so /usr/lib64/freeradius/</p><p>wget -O /etc/yum.repos.d/epel7.repo <a href="https://mirrors.aliyun.com/repo/epel-7.repo" target="_blank" rel="noreferrer">https://mirrors.aliyun.com/repo/epel-7.repo</a></p><p><a href="https://github.com/privacyidea/FreeRADIUS" target="_blank" rel="noreferrer">https://github.com/privacyidea/FreeRADIUS</a></p><p><a href="https://draculaservers.com/tutorials/freeradius-centos-7-mysql/" target="_blank" rel="noreferrer">https://draculaservers.com/tutorials/freeradius-centos-7-mysql/</a></p>',7),s=[o];function n(h,l,p,A,c,m){return r(),t("div",null,s)}const d=e(i,[["render",n]]);export{_ as __pageData,d as default};
