import{_ as a,o as l,c as e,R as i}from"./chunks/framework.zUbWieqp.js";const S=JSON.parse('{"title":"1. AWS WAF（Web 应用程序防火墙）","description":"","frontmatter":{},"headers":[],"relativePath":"guide/cloud/aws/security/3-aws_waf.md","filePath":"guide/cloud/aws/security/3-aws_waf.md","lastUpdated":1743777336000}'),t={name:"guide/cloud/aws/security/3-aws_waf.md"},o=i('<h1 id="_1-aws-waf-web-应用程序防火墙" tabindex="-1">1. AWS WAF（Web 应用程序防火墙） <a class="header-anchor" href="#_1-aws-waf-web-应用程序防火墙" aria-label="Permalink to &quot;1. AWS WAF（Web 应用程序防火墙）&quot;">​</a></h1><h2 id="_1-1-介绍" tabindex="-1">1.1 介绍 <a class="header-anchor" href="#_1-1-介绍" aria-label="Permalink to &quot;1.1 介绍&quot;">​</a></h2><h2 id="_1-2-特点" tabindex="-1">1.2 特点 <a class="header-anchor" href="#_1-2-特点" aria-label="Permalink to &quot;1.2 特点&quot;">​</a></h2><ul><li>保护您的WEB应用程序免受第7层的常见WEB漏洞攻击</li><li>部署在应用程序负载平衡器（本地化的规则）</li><li>部署在API网关（以使规则在区域或边缘级别运行）</li><li>部署在CloudFront（规则在全球范围内的边缘节点上运行） <ul><li>可用于其他服务前端（经典负载平衡器、EC2实例、自定义源站或S3网站）</li></ul></li><li>部署在AppSync（保护您的GraphQLAPI)</li><li>WAF不用于DDoS防护</li><li>定义WEBACL（Web访问控制列表） <ul><li>规则可以包括IP地址过滤、HTTP头、HTTP正文、URL字符串</li><li>抵御SQL注入或跨站点脚本、XSS等常见攻击防护</li><li>大小约束以及Geo匹配</li><li>基于速率的规则（基于事件发生的频次）</li></ul></li><li>有190多条托管规则</li><li>ready-to-use托管规则-是AwS或者是Marketplace提供的</li><li>基准托管规则组－可针对多种常见威胁提供常规保护 <ul><li>wSManagedRulesCommonRuleSet,AWSManagedRulesAdminProtectionRuleSet</li></ul></li><li>特定用例规则组-为多种用例场景给与保护 <ul><li>AWSManagedRulesSQLiRuleSet、AWSManagedRulesWindowsRuleSet</li><li>AWSManagedRulesPHPRuleSet,AWSManagedRulesWordPressRuleSet</li></ul></li><li>IP声誉规则组-允许您根据请求源IP阻止请求（例如，恶意IP地址） <ul><li>awSManagedRulesAmazonlpReputationList,AWSManagedRulesAnonymouslpList</li></ul></li><li>机器人控制规则组-用于阻止和管理来自Bot机器人的请求</li></ul><h1 id="_2-aws-waf的web-acl日志" tabindex="-1">2. AWS WAF的Web ACL日志 <a class="header-anchor" href="#_2-aws-waf的web-acl日志" aria-label="Permalink to &quot;2. AWS WAF的Web ACL日志&quot;">​</a></h1><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202504041905881.png" alt="image-20250404190520254"></p><h1 id="_3-aws-firewall-manager" tabindex="-1">3. AWS Firewall Manager <a class="header-anchor" href="#_3-aws-firewall-manager" aria-label="Permalink to &quot;3. AWS Firewall Manager&quot;">​</a></h1><ul><li>管理一个AWSOrganization的所有帐户中的所有规则</li><li>定义适用于WAF的通用的安全规则</li><li>WAF规则、应用程序负载均衡器、API网关和CloudFront)</li><li>AWS Shield Advanced（ALB、CLB、弹性IP地址和CloudFront）</li><li>EC2的安全组以及VPC的ENI</li></ul><p><a href="https://senjianlu.com/2024/11/25/knowledge_cloud_security/" target="_blank" rel="noreferrer">https://senjianlu.com/2024/11/25/knowledge_cloud_security/</a></p>',9),s=[o];function r(n,u,d,c,_,A){return l(),e("div",null,s)}const w=a(t,[["render",r]]);export{S as __pageData,w as default};
