import{_ as a,o as i,c as l,R as e}from"./chunks/framework.zUbWieqp.js";const S=JSON.parse('{"title":"1. Cloud Security","description":"","frontmatter":{},"headers":[],"relativePath":"guide/cloud/aws/security/index.md","filePath":"guide/cloud/aws/security/index.md","lastUpdated":1743777336000}'),o={name:"guide/cloud/aws/security/index.md"},t=e('<h1 id="_1-cloud-security" tabindex="-1">1. Cloud Security <a class="header-anchor" href="#_1-cloud-security" aria-label="Permalink to &quot;1. Cloud Security&quot;">​</a></h1><h2 id="_1-1-网络安全基本组成" tabindex="-1">1.1 网络安全基本组成 <a class="header-anchor" href="#_1-1-网络安全基本组成" aria-label="Permalink to &quot;1.1 网络安全基本组成&quot;">​</a></h2><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202504041846507.png" alt="image-20250404184613108"></p><h3 id="安全组" tabindex="-1">安全组 <a class="header-anchor" href="#安全组" aria-label="Permalink to &quot;安全组&quot;">​</a></h3><ul><li>附加到ENI(弹性网络接口）-VPC内部的EC2、RDS、Lambda等</li><li>有状态的（任何进入的通信都允许返回，任何传出的通信也都允许返回）</li><li>可使用安全组ID以及CIDR引I用规则</li><li>支持VPC对等连接侧的安全组的引用</li><li>默认：入站被拒绝，所有的出站全部被允许</li></ul><h3 id="网络acl-nacl" tabindex="-1">网络ACL(NACL) <a class="header-anchor" href="#网络acl-nacl" aria-label="Permalink to &quot;网络ACL(NACL)&quot;">​</a></h3><ul><li>附加到子网上</li><li>无状态的（入站规则和出站规则应用于所有通信）</li><li>只能通过CIDR块范围引用主机防</li><li>默认：允许所有入站，允许所有的出站</li><li>新NACL：拒绝所有入站，拒绝所有的出站</li></ul><h3 id="主机防火墙" tabindex="-1">主机防火墙 <a class="header-anchor" href="#主机防火墙" aria-label="Permalink to &quot;主机防火墙&quot;">​</a></h3><p>⭐ <code>网络 ACL (NACL)</code> 位于子网级别</p><h2 id="_1-2-网络攻击类型" tabindex="-1">1.2 网络攻击类型 <a class="header-anchor" href="#_1-2-网络攻击类型" aria-label="Permalink to &quot;1.2 网络攻击类型&quot;">​</a></h2><h3 id="ddos" tabindex="-1">DDos <a class="header-anchor" href="#ddos" aria-label="Permalink to &quot;DDos&quot;">​</a></h3><ul><li>服务会接收到大量的并发请求，使服务不可用</li><li>SYNFlood（4层）：发送大量的TCP连接请求时</li><li>UDP反射：通过访问其他服务器然后发送非常大的UDP请求</li><li>DNS洪水攻击：淹没DNS服务使用户访问不了网站</li><li>Slowloris攻击：使服务器同时打开并维持许多的HTTP连接，从而来压垮服务器</li></ul><h3 id="应用程序级别攻击" tabindex="-1">应用程序级别攻击 <a class="header-anchor" href="#应用程序级别攻击" aria-label="Permalink to &quot;应用程序级别攻击&quot;">​</a></h3><ul><li>攻击更具体，更复杂（7层）</li><li>缓存突发策略：通过请求无效缓存使后端的数据库过载</li></ul><h2 id="_1-3-aws的ddos防护" tabindex="-1">1.3 AWS的DDoS防护 <a class="header-anchor" href="#_1-3-aws的ddos防护" aria-label="Permalink to &quot;1.3 AWS的DDoS防护&quot;">​</a></h2><ul><li>AWS Shield Standard：保护您的网站应用程序免受DDoS攻击，为所有客户提供免费服务</li><li>AWS Shield Advanced：提供24/7的高级DDoS防护</li><li>AWS WAF：根据规则过滤特定请求</li><li>CloudFront和Route53： <ul><li>用全球边缘网络给予可用性保护</li><li>与Shield结合使用，直接在网络边缘节点提供缓解DDoS攻击</li></ul></li><li>弹性扩展：利用AWSAutoScaling</li><li>将静态资源（S3/CloudFront）与动态请求（EC2/ALB)分离</li></ul><p><a href="https://docs.aws.amazon.com/zh_cn/whitepapers/latest/aws-best-practices-ddos-resiliency/aws-best-practices-ddos-resiliency.pdf" target="_blank" rel="noreferrer">https://docs.aws.amazon.com/zh_cn/whitepapers/latest/aws-best-practices-ddos-resiliency/aws-best-practices-ddos-resiliency.pdf</a></p><h3 id="防护的架构示例" tabindex="-1">防护的架构示例 <a class="header-anchor" href="#防护的架构示例" aria-label="Permalink to &quot;防护的架构示例&quot;">​</a></h3><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202504041851937.png" alt="AWS 针对 DDoS 防护的架构示例"></p>',19),s=[t];function r(d,c,n,h,u,_){return i(),l("div",null,s)}const m=a(o,[["render",r]]);export{S as __pageData,m as default};
