import{_ as s,o as a,c as e,R as n}from"./chunks/framework.zUbWieqp.js";const g=JSON.parse('{"title":"1. 下载kubectl","description":"","frontmatter":{},"headers":[],"relativePath":"guide/container/k8s/2-win_install_kubectl.md","filePath":"guide/container/k8s/2-win_install_kubectl.md","lastUpdated":1716514242000}'),o={name:"guide/container/k8s/2-win_install_kubectl.md"},t=n(`<h1 id="_1-下载kubectl" tabindex="-1">1. 下载kubectl <a class="header-anchor" href="#_1-下载kubectl" aria-label="Permalink to &quot;1. 下载kubectl&quot;">​</a></h1><p><a href="https://kubernetes.io/zh-cn/docs/tasks/tools/install-kubectl-windows/" target="_blank" rel="noreferrer">https://kubernetes.io/zh-cn/docs/tasks/tools/install-kubectl-windows/</a></p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">curl</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-LO</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">&quot;https://dl.k8s.io/release/v1.29.1/bin/windows/amd64/kubectl.exe&quot;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">curl</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-LO</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&quot;https://dl.k8s.io/release/v1.29.1/bin/windows/amd64/kubectl.exe&quot;</span></span></code></pre></div><blockquote><p>放到你创建好的目录下</p></blockquote><h2 id="_1-1-系统配置" tabindex="-1">1.1 系统配置 <a class="header-anchor" href="#_1-1-系统配置" aria-label="Permalink to &quot;1.1 系统配置&quot;">​</a></h2><p>按下 Win + R 打开运行对话框，输入 sysdm.cpl</p><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202405221121396.png" alt="image-20240522112125541"></p><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202405221122986.png" alt="image-20240522112220077"></p><p>你可以选择在系统或者个人目录下</p><h2 id="_1-2-配置config" tabindex="-1">1.2 配置config <a class="header-anchor" href="#_1-2-配置config" aria-label="Permalink to &quot;1.2 配置config&quot;">​</a></h2><p>在系统用户的家目录下，创建个 <strong>.kube</strong> 的目录，接着在这个目录下创建个 <strong>config</strong> 文件</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">mkdir</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">.kube</span></span>
<span class="line"><span style="color:#79B8FF;">cd</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">.kube</span></span>
<span class="line"><span style="color:#B392F0;">touch</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">config</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">mkdir</span><span style="color:#24292E;"> </span><span style="color:#032F62;">.kube</span></span>
<span class="line"><span style="color:#005CC5;">cd</span><span style="color:#24292E;"> </span><span style="color:#032F62;">.kube</span></span>
<span class="line"><span style="color:#6F42C1;">touch</span><span style="color:#24292E;"> </span><span style="color:#032F62;">config</span></span></code></pre></div><p>将 Kubernetes 集群 master 节点上 <strong>/etc/kubernetes/admin.conf</strong> 文件的内容 copy 到刚才创建的 <strong>config</strong> 文件中即可</p><ul><li>验证</li></ul><p><img src="https://nnaigos.oss-cn-hangzhou.aliyuncs.com/imgs/202405221128240.png" alt="image-20240522112842595"></p><h4 id="说明" tabindex="-1">说明： <a class="header-anchor" href="#说明" aria-label="Permalink to &quot;说明：&quot;">​</a></h4><p><a href="https://docs.docker.com/docker-for-windows/#kubernetes" target="_blank" rel="noreferrer">Windows 版的 Docker Desktop</a> 将其自带版本的 <code>kubectl</code> 添加到 <code>PATH</code>。 如果你之前安装过 Docker Desktop，可能需要把此 <code>PATH</code> 条目置于 Docker Desktop 安装的条目之前， 或者直接删掉 Docker Desktop 的 <code>kubectl</code>,或者修改名字</p>`,17),l=[t];function c(p,r,i,d,u,h){return a(),e("div",null,l)}const b=s(o,[["render",c]]);export{g as __pageData,b as default};
