import{_ as e,o as a,c as s,R as t}from"./chunks/framework.zUbWieqp.js";const b=JSON.parse('{"title":"1. Lens 介绍","description":"","frontmatter":{},"headers":[],"relativePath":"guide/container/k8s/4-lensDashboard.md","filePath":"guide/container/k8s/4-lensDashboard.md","lastUpdated":1721730545000}'),o={name:"guide/container/k8s/4-lensDashboard.md"},n=t('<h1 id="_1-lens-介绍" tabindex="-1">1. Lens 介绍 <a class="header-anchor" href="#_1-lens-介绍" aria-label="Permalink to &quot;1. Lens 介绍&quot;">​</a></h1><p><code>Lens</code> 是一个强大的 kubernetes IDE。可以实时查看 kubernetes 集群状态，比如 Pod实时日志查看、集群Events实时查看、集群故障排查等。有了 Lens，不在需要敲打很长的 kubectl 命令，只要使用鼠标点击几下，非常便捷。</p><p><code>Lens</code> 支持多平台安装，目前支持 <code>Linux</code>、<code>MacOS</code>、<code>Windows</code>。</p><h2 id="_1-1-优势" tabindex="-1">1.1 优势 <a class="header-anchor" href="#_1-1-优势" aria-label="Permalink to &quot;1.1 优势&quot;">​</a></h2><ul><li>用户体验性和可用性非常好</li><li>多集群管理；支持数百个集群</li><li>独立应用程序；无需在集群中安装任何东西</li><li>集群状态实时可视化</li><li>内置 <code>Prometheus</code> 提供资源利用率图表和历史趋势图表</li><li>提供终端访问节点和容器</li><li>性能经过优化，可应用于大规模集群（已在25k pod的集群进行了测试）</li><li>完全支持 Kubernetes <code>RBAC</code></li></ul><h1 id="_2-lens部署" tabindex="-1">2. Lens部署 <a class="header-anchor" href="#_2-lens部署" aria-label="Permalink to &quot;2. Lens部署&quot;">​</a></h1><p>文档,<a href="https://github.com/lensapp/lens" target="_blank" rel="noreferrer">https://github.com/lensapp/lens</a></p>',7),l=[n];function r(i,d,c,_,h,p){return a(),s("div",null,l)}const m=e(o,[["render",r]]);export{b as __pageData,m as default};
